INSERT INTO catalog7_yandex.marks VALUES(205, 'Peugeot', 'Пежо', 'peugeot', 'В настоящее время компания занимает второе место в Европе по объемам производства автомобилей. Основным преимуществом, которое привлекает множество клиентов, является невысокая стоимость Peugeot. Руководство компании уделяет много внимания выпуску машин компакт и среднего класса.  Именно цена на Пежо – одна из главных составляющих успеха компании. Стоит отметить, что изначально концерн специализировался далеко не на производстве автомобилей. Она выпускала кольца «панье» для женских платьев, затем кольца для зонтиков. Первым транспортным средством, выпущенным на предприятии, был велосипед.   Если сравнить, сколько стоит Peugeot и его «одноклассники» от других производителей, то можно заметить, что в этом вопросе машины этой марки имеют заметное преимущество. Что касается надежности, то здесь автомобиль Пежо отрабатывает свои деньги на все сто процентов.', 1, 1, 0, 10, 'PEUGEOT', 205);
