INSERT INTO catalog7_yandex.marks VALUES(15, 'Audi', 'Ауди', 'audi', 'Автомобили марки Audi – это символ немецкого непревзойденного качества, представленный в широком модельном ряде. На сегодняшний день автомобильная марка Ауди насчитывает порядка десяти моделей. В ближайшее время этот ряд различных типов авто пополнится ещё семью моделями. Купить Audi – значит сделать правильный выбор в сторону качества и лучших традиций немецкого автопроизводства. Постоянный рост оборотов позволяет этой марке выпускать различные типы авто с отличными характеристиками техники и дизайна.', 1, 1, 0, 6, 'AUDI', 15);
