INSERT INTO catalog7_yandex.folders VALUES(24797, 45969, 232, 237, 1, 0, 15, 'Q5', 'Q5', 'q5', 'Автомобиль Audi Q5 получил прозвище самого спортивного и динамичного кроссовера из всех тех, что имеются в данном классе. Его комфортно использовать за городом и в границах мегаполиса. Плавное переключение скоростей, идеальная шумоизоляция и новейшие системы безопасности – это далеко не все, чем может похвастаться Ауди Q5.  Данная модель Audi дебютировала в Пекине в 2008 году, а производство его было налажено в Германии, Китае и Индии. Премьера была настолько удачной, что Q5 переманила на свою сторону любителей других классов авто конкурентных компаний. В сравнении с Ауди Q7 модель Q5 выделялась меньшими размерами и большей экономичностью.', 0, 0, 'group', 19940, 'Q5', 24797, null, null);
