INSERT INTO catalog7_yandex.marks VALUES(170, 'Mercedes-Benz', 'Мерседес-Бенц', 'mercedes', 'История этой марки насчитывает более 100 лет. Еще в 1886 году Карлом Бенцем была выпущена первая в мире тележка на трех колесах, оснащенная бензиновым двигателем. Сегодня любой автомобилист желает купить Mercedes-Benz.  Все без исключения автомобили Мерседес-Бенц, независимо от класса, к которому они относятся, отличаются  высоким качеством исполнения и надежностью в процессе эксплуатации. Практически в каждом классе компания имеет флагмана, являющегося примером для других производителей.', 1, 1, 0, 6, 'MERCEDES', 170);
