INSERT INTO catalog7_yandex.folders VALUES(4452, 46119, 280, 287, 1, 0, 170, 'GL-klasse', 'GL-класс', 'gl-klasse', 'Начиная с 2006 года, компания выпускает на рынок новую звезду класса SUV или Mercedes-Benz GL-klasse. Просторность и комфорт органично сочетается с высокой динамикой модели, в результате чего получается роскошный кроссовер. Мерседес-Бенц GL-класса не только исключительно устроен внутри, но и имеет импозантный экстерьер.  Более важно другое: впервые автомобили такого класса оснащаются системой безопасности PRE-SAFE. В дополнение к этому Mercedes-Benz GL-klasse комплектуется системами стабилизации при боковом ветре и помощи водителю в управлении рулем, а также Collision Prevention Assist – разработкой, препятствующей аварии. В списке полезных бонусов Мерседес Бенц GL-класса – система контроля за «мертвыми» зонами.', 0, 0, 'group', 1647, 'GL_KLASSE', 4452, null, null);
