INSERT INTO catalog7_yandex.folders VALUES(245, 45969, 280, 295, 1, 0, 15, 'A6', 'А6', 'a6', 'Продолжением Audi 100, дебютировавшей в 1968 году, стало авто бизнес класса Audi A6. После глобального рестайлинга 1994 года модель изменила форму багажника и капота, обзавелась новыми двигателями (ТD 1,9, бензиновый 1,8 л.) и дизайном. В настоящее время Ауди А6 выпускается с двумя типами кузовов – четырехдверный седан и пятидверный универсал. Существует также спортивная версия – S6, и версия с полным приводом – quattro.  Audi A6 в последнем поколении стали более просторными и укомплектованными техническими новинками. Среди последних усовершенствований Ауди А6 выделяют сенсорную панель MMI touch, систему ESP со спортивной настройкой.', 0, 0, 'group', 81, 'A6', 245, null, null);
