INSERT INTO catalog7_yandex.marks VALUES(147, 'Land Rover', 'Ленд Ровер', 'land-rover', 'Автомобили Land Rover стоят отдельным рядом в общей картине мирового автомобилестроения. Эта британская марка носит узкую специализацию: Ленд Ровер авто повышенной проходимости класса «премиум». Одним из первых заводов, производивших авто этой марки стал завод, выпускавший двигатели для танков и самолетов. На его мощностях и началась история производства Land Rover, надежность которого поражает до сих пор. И несмотря на то что модельный ряд представлен всего шестью моделями, эта марка пользуется популярностью среди авто профессионалов и любителей по всему миру. Стоимость  Ленд Ровер полностью соответствует его великолепным характеристикам.', 1, 1, 0, 28, 'LAND_ROVER', 147);
