// every reaction publishes one or more artifacts
// so we get the one of them (max, for example)
let source = series_max({project="reactor", cluster="prod", service="prod_push", mode="usertime", path="/test", attribute="delay_first_sec", sensor="artifact*"});

let now = time_interval_end(); // in UTC epoch seconds 
let last_point_timestamp = last(get_timestamps(source)); // last timestamp

let delay_hours = (now - last_point_timestamp) / 3600;

let ALERT_HOURS = 1;
alarm_if(last_point_timestamp != last_point_timestamp); // last_point_timestamp is NaN, happens when no points are in window
let is_not_published = delay_hours > ALERT_HOURS;

alarm_if(is_not_published);
