// every reaction publishes one or more artifacts
// so we get the one of them (max, for example)
let source = series_max({project="reactor", cluster="prod", service="prod_push", mode="usertime", path="/test", attribute="delay_first_sec", sensor="artifact*"});

let time_offset = 3 * 3600; // UTC + 3 hours = MSK time
let now = time_interval_end() + time_offset; // in UTC epoch seconds 
let today = now - 86400 * fract(now / 86400); // start of the day second
let now_hours = (now - today) / 3600;

let last_point_timestamp = last(get_timestamps(source));
let last_value = last(drop_nan(source));
let completed_hours = (last_point_timestamp + time_offset + last_value - today) / 3600;
let completed_hours_str = to_fixed(completed_hours, 1);

let ALERT_HOURS = 1;
let is_not_published = now_hours > ALERT_HOURS && completed_hours < 0; // negative completed_hours means there are no point today

alarm_if(is_not_published);
