open class TotalActionLimits: ActionLimitsStrategy {
  private var totalLimit: Int32
  public init(_ totalLimit: Int32) {
    self.totalLimit = totalLimit
  }

  @discardableResult
  open func check(_ actions: Stack<MBTAction>) -> Bool {
