public open class MinimizeKeyboardAction(): MBTAction {
    open override fun supported(modelFeatures: YSArray<FeatureID>, applicationFeatures: YSArray<FeatureID>): Boolean {
        return KeyboardFeature.`get`.includedAll(modelFeatures, applicationFeatures)
    }

    open override fun canBePerformed(model: App): Boolean {
        val modelKeyboard = KeyboardFeature.`get`.forceCast(model)
        return modelKeyboard.isKeyboardShown()
    }

    open override fun perform(model: App, application: App, history: MBTHistory): MBTComponent {
        val modelKeyboard = KeyboardFeature.`get`.forceCast(model)
        val appKeyboard = KeyboardFeature.`get`.forceCast(application)
        modelKeyboard.minimizeKeyboard()
        appKeyboard.minimizeKeyboard()
        return history.currentComponent
    }

    open override fun events(): YSArray<EventusEvent> {
        return mutableListOf()
    }

    open override fun tostring(): String {
        return "MinimizeKeyboardAction"
    }

    open override fun getActionType(): String {
        return MinimizeKeyboardAction.type
    }

    companion object {
        @JvmStatic val type: MBTActionType = "MinimizeKeyboardAction"
    }
}

