public open class UnbindCardAction(private var index: Int): BaseSimpleAction<UnbindCard, UnbindCardComponent>(UnbindCardAction.type) {
    open override fun requiredFeature(): Feature<UnbindCard> {
        return UnbindCardFeature.`get`
    }

    open override fun performImpl(modelOrApplication: UnbindCard, currentComponent: UnbindCardComponent): MBTComponent {
        val numberOfCards = modelOrApplication.getCards().size
        modelOrApplication.unbindCard(this.index)
        return if (numberOfCards - 1 > 0) UnbindCardComponent() else PaymentMethodSelectedComponent()
    }

    open override fun events(): YSArray<EventusEvent> {
        return mutableListOf()
    }

    companion object {
        @JvmStatic val type: MBTActionType = "UnbindCardAction"
    }
}

