@discardableResult
public func decodeInitPaymentRequest(_ item: JSONItem) -> Result<InitPaymentRequest> {
  return decodeJSONItem(item, {
    (json) in
    let map = (try json.tryCastAsMapJSONItem())
    let appInfo = InitializationParams(map.getString("psuid"), map.getString("tsid"), map.getString("turboapp_id"))
    return InitPaymentRequest((try map.tryGetString("token")), map.getString("email"), (try map.tryGetBoolean("credit")), appInfo)
  })
}

@discardableResult
