import Foundation

open class SpamableBackend: Spamable {
  private var messageListDisplayBackend: MessageListDisplayBackend
  private var clientsHandler: MailboxClientHandler
  public init(_ messageListDisplayBackend: MessageListDisplayBackend, _ clientsHandler: MailboxClientHandler) {
    self.messageListDisplayBackend = messageListDisplayBackend
    self.clientsHandler = clientsHandler
  }

  @discardableResult
  open func moveToSpam(_ order: Int32) throws -> Void {
    let fid = self.messageListDisplayBackend.getCurrentFolderId()
    let tid = self.messageListDisplayBackend.getThreadMessage(order).tid!
    self.clientsHandler.getCurrentClient().moveToSpam(fid, tid)
  }

  @discardableResult
  open func moveFromSpam(_ order: Int32) throws -> Void {
    let fid = self.messageListDisplayBackend.getInbox().fid
    let tid = requireNonNull(self.messageListDisplayBackend.getThreadMessage(order).tid, "message must have tid!")
    self.clientsHandler.getCurrentClient().moveThreadToFolder(fid, tid)
  }

}

