public open class MoveToFolderComponent: MBTComponent {
    open override fun assertMatches(model: App, application: App): Unit {
        val modelMoveToFolder = MoveToFolderFeature.`get`.castIfSupported(model)
        val appMoveToFolder = MoveToFolderFeature.`get`.castIfSupported(application)
        if (modelMoveToFolder == null || appMoveToFolder == null) {
            return
        }
        val modelFolderList = modelMoveToFolder.getFolderList()
        val appFolderList = appMoveToFolder.getFolderList()
        for (folder in modelFolderList) {
            assertTrue(appFolderList.contains(folder), "There is no folder ${folder}")
        }
    }

    open override fun tostring(): String {
        return this.getComponentType()
    }

    open override fun getComponentType(): MBTComponentType {
        return MoveToFolderComponent.type
    }

    companion object {
        @JvmStatic val type: MBTComponentType = "MoveToFolderComponent"
    }
}

