open class SelectParentFolderComponent: MBTComponent {
  public static let type: String = "SelectParentFolderComponent"
  @discardableResult
  open func getComponentType() -> String {
    return SelectParentFolderComponent.type
  }

  @discardableResult
  open func assertMatches(_ model: App, _ application: App) throws -> Void {
    let manageableFolderModel: ManageableFolder! = ManageableFolderFeature.`get`.castIfSupported(model)
    let manageableFolderApplication: ManageableFolder! = ManageableFolderFeature.`get`.castIfSupported(application)
    if manageableFolderModel != nil && manageableFolderApplication != nil {
      let folderListModel = (try manageableFolderModel.getFolderListForFolderLocationScreen()).map({
        (folder) in
        folder.split("|").reverse()[0]
      })
      let folderListApplication = (try manageableFolderApplication.getFolderListForFolderLocationScreen()).map({
        (folder) in
        folder.split("|").reverse()[0]
      })
      (try assertInt32Equals(folderListModel.length, folderListApplication.length, "Different number of folders"))
      for folder in folderListModel {
        (try assertBooleanEquals(true, folderListApplication.includes(folder), "Missing folder \(folder)"))
      }
    }
  }

  @discardableResult
  open func tostring() -> String {
    return self.getComponentType()
  }

}

