import Foundation

open class SourceLanguageListComponent: MBTComponent {
  public static let type: String = "TranslatorSourceLanguageListComponent"
  @discardableResult
  open func getComponentType() -> MBTComponentType {
    return SourceLanguageListComponent.type
  }

  @discardableResult
  open func assertMatches(_ model: App, _ application: App) throws -> Void {
    let modelTranslatorLanguageList: TranslatorLanguageList! = TranslatorLanguageListFeature.`get`.castIfSupported(model)
    let appTranslatorLanguageList: TranslatorLanguageList! = TranslatorLanguageListFeature.`get`.castIfSupported(application)
    if modelTranslatorLanguageList != nil && appTranslatorLanguageList != nil {
      let modelCurrentSourceLanguage: String! = (try modelTranslatorLanguageList.getCurrentSourceLanguage())
      let appCurrentSourceLanguage: String! = (try appTranslatorLanguageList.getCurrentSourceLanguage())
      (try assertTrue(modelCurrentSourceLanguage == appCurrentSourceLanguage, "Current source language is incorrect"))
      let modelDeterminedAutomaticallySourceLanguage = (try modelTranslatorLanguageList.getDeterminedAutomaticallySourceLanguage())
      let appDeterminedAutomaticallySourceLanguage = (try appTranslatorLanguageList.getDeterminedAutomaticallySourceLanguage())
      (try assertStringEquals(modelDeterminedAutomaticallySourceLanguage, appDeterminedAutomaticallySourceLanguage, "Determined automatically source language is incorrect"))
      let modelRecentSourceLanguages = (try modelTranslatorLanguageList.getRecentSourceLanguages())
      let appRecentSourceLanguages = (try appTranslatorLanguageList.getRecentSourceLanguages())
      (try assertInt32Equals(modelRecentSourceLanguages.length, appRecentSourceLanguages.length, "Incorrect number of recent languages"))
      for modelLanguage in modelRecentSourceLanguages {
        (try assertTrue(appRecentSourceLanguages.includes(modelLanguage), "Application list of recent source languages not include \(modelLanguage)"))
      }
    }
  }

