import Foundation

open class MarkableReadModel: MarkableRead {
  private var model: MessageListDisplayModel
  private var accHandler: MailAppModelHandler
  public init(_ model: MessageListDisplayModel, _ accHandler: MailAppModelHandler) {
    self.model = model
    self.accHandler = accHandler
  }

  @discardableResult
  open func markAsRead(_ order: Int32) throws -> Void {
    for mid in self.model.getThreadByOrder(order) {
      self.accHandler.getCurrentAccount().messagesDB.storedMessage(mid).mutableHead.read = true
    }
  }

  @discardableResult
  open func markAsUnread(_ order: Int32) throws -> Void {
    for mid in self.model.getThreadByOrder(order) {
      self.accHandler.getCurrentAccount().messagesDB.storedMessage(mid).mutableHead.read = false
    }
  }

}

