public open class MovableToFolderModel(private var model: MessageListDisplayModel, private var accHandler: MailAppModelHandler): MovableToFolder {
    open override fun moveMessageToFolder(order: Int, folderName: FolderName): Unit {
        this.moveMessagesToFolder(YSSet<Int>(mutableListOf(order)), folderName)
    }

    open fun moveMessagesToFolder(orders: YSSet<Int>, folderName: FolderName): Unit {
        for (mid in this.model.getMidsByOrders(orders)) {
            this.accHandler.getCurrentAccount().messagesDB.moveMessageToFolder(mid, folderName)
        }
    }

}

