import Foundation

open class MovableToFolderModel: MovableToFolder {
  private var model: MessageListDisplayModel
  private var accHandler: MailAppModelHandler
  public init(_ model: MessageListDisplayModel, _ accHandler: MailAppModelHandler) {
    self.model = model
    self.accHandler = accHandler
  }

  open func moveMessageToFolder(_ order: Int32, _ folderName: FolderName) -> Void {
    self.moveMessagesToFolder(YSSet<Int32>(YSArray(order)), folderName)
  }

  open func moveMessagesToFolder(_ orders: YSSet<Int32>, _ folderName: FolderName) -> Void {
    for mid in self.model.getMidsByOrders(orders) {
      self.accHandler.getCurrentAccount().messagesDB.moveMessageToFolder(mid, folderName)
    }
  }

}

