public fun allActionsBehaviour(accounts: YSArray<UserAccount>): UserBehaviour {
    return singleAccountBehaviour().setActionProvider(LoginComponent.type, AllLoginActions(accounts))
}

public fun singleAccountBehaviour(): UserBehaviour {
    return UserBehaviour().setActionProvider(MaillistComponent.type, AllMaillistActions()).setActionProvider(GroupOperationsComponent.type, AllGroupOperationsActions()).setActionProvider(MessageComponent.type, AllMessageActions())
}

public fun readUnreadUser(account: UserAccount): UserBehaviour {
    return allActionsBehaviour(mutableListOf(account)).setActionProvider(LoginComponent.type, AllLoginActions(mutableListOf(account))).whitelistFor(MaillistComponent.type, MarkAsRead.type).whitelistFor(MaillistComponent.type, MarkAsUnread.type)
}

