import Foundation

@discardableResult
public func allActionsBehaviour(_ accounts: YSArray<UserAccount>) -> UserBehaviour {
  return singleAccountBehaviour().setActionProvider(LoginComponent.type, AllLoginActions(accounts))
}

@discardableResult
public func singleAccountBehaviour() -> UserBehaviour {
  return UserBehaviour().setActionProvider(MaillistComponent.type, AllMaillistActions()).setActionProvider(GroupOperationsComponent.type, AllGroupOperationsActions()).setActionProvider(MessageComponent.type, AllMessageActions())
}

@discardableResult
public func readUnreadUser(_ account: UserAccount) -> UserBehaviour {
  return allActionsBehaviour(YSArray(account)).setActionProvider(LoginComponent.type, AllLoginActions(YSArray(account))).whitelistFor(MaillistComponent.type, MarkAsRead.type).whitelistFor(MaillistComponent.type, MarkAsUnread.type)
}

