package com.yandex.xplat.testopithecus.common

import com.yandex.xplat.common.*
import com.yandex.xplat.eventus.common.*
import com.yandex.xplat.eventus.*

public interface RandomProvider {
    fun generate(n: Int): Int
}

public fun randomInt(random: RandomProvider, min: Int, max: Int): Int {
    return random.generate(max - min) + min
}

public fun randomInterval(random: RandomProvider, min: Int, max: Int): YSArray<Int> {
    val first = randomInt(random, min, max)
    val second = randomInt(random, min, max)
    if (first > second) {
        return mutableListOf(second, first)
    } else {
        return mutableListOf(first, second)
    }
}

