package com.yandex.xplat.testopithecus

import com.yandex.xplat.common.*
import com.yandex.xplat.eventus.common.*
import com.yandex.xplat.eventus.*
import com.yandex.xplat.mapi.*
import com.yandex.xplat.testopithecus.common.*

public open class GoToLabelAction(private var label: LabelName): MBTAction {
    open override fun supported(modelFeatures: YSArray<FeatureID>, applicationFeatures: YSArray<FeatureID>): Boolean {
        return LabelNavigatorFeature.`get`.includedAll(modelFeatures, applicationFeatures)
    }

    open override fun canBePerformed(model: App): Boolean {
        val labelNavigatorModel = LabelNavigatorFeature.`get`.forceCast(model)
        val labels = labelNavigatorModel.getLabelList()
        return labels.filter( {
            label ->
            label == this.label
        }).size > 0
    }

    open override fun perform(model: App, application: App, _history: MBTHistory): MBTComponent {
        LabelNavigatorFeature.`get`.forceCast(model).goToLabel(this.label)
        LabelNavigatorFeature.`get`.forceCast(application).goToLabel(this.label)
        return MaillistComponent()
    }

    open override fun events(): YSArray<EventusEvent> {
        return mutableListOf()
    }

    open override fun getActionType(): String {
        return GoToLabelAction.type
    }

    open override fun tostring(): String {
        return "GoToLabel(${this.label})"
    }

    companion object {
        @JvmStatic val type: MBTActionType = "GoToLabel"
    }
}

