public open class SpamableBackend(private var messageListDisplayBackend: MessageListDisplayBackend, private var clientsHandler: MailboxClientHandler): Spamable {
    open override fun moveToSpam(order: Int): Unit {
        val fid = this.messageListDisplayBackend.getCurrentFolderId()
        val tid = this.messageListDisplayBackend.getThreadMessage(order).tid!!
        this.clientsHandler.getCurrentClient().moveToSpam(fid, tid)
    }

    open override fun moveFromSpam(order: Int): Unit {
        val fid = this.messageListDisplayBackend.getInbox().fid
        val tid = requireNonNull(this.messageListDisplayBackend.getThreadMessage(order).tid, "message must have tid!")
        this.clientsHandler.getCurrentClient().moveThreadToFolder(fid, tid)
    }

}

