            }
        }
        TabBarComponent().assertMatches(model, application)
    }

    open override fun tostring(): String {
        return "ZeroSuggestComponent"
    }

    open override fun getComponentType(): String {
        return ZeroSuggestComponent.type
    }

    companion object {
