public open class ManageLabelsComponent: MBTComponent {
    open override fun getComponentType(): String {
        return ManageLabelsComponent.type
    }

    open override fun assertMatches(model: App, application: App): Unit {
        val manageableLabelModel = ManageableLabelFeature.`get`.castIfSupported(model)
        val manageableLabelApplication = ManageableLabelFeature.`get`.castIfSupported(application)
        if (manageableLabelModel != null && manageableLabelApplication != null) {
            val labelListModel = manageableLabelModel.getLabelList()
            val labelListApplication = manageableLabelApplication.getLabelList()
            assertInt32Equals(labelListModel.size, labelListApplication.size, "Different number of labels")
            for (label in labelListModel) {
                assertBooleanEquals(true, labelListApplication.contains(label), "Missing label ${label}")
            }
        }
    }

    open override fun tostring(): String {
        return this.getComponentType()
    }

    companion object {
        @JvmStatic val type: String = "ManageLabelsComponent"
    }
}

public open class ManageLabelsActions: MBTComponentActions {
    open override fun getActions(_model: App): YSArray<MBTAction> {
        val actions: YSArray<MBTAction> = mutableListOf()
        return actions
    }

