public open class AboutSettingsComponent: MBTComponent {
    open override fun assertMatches(model: App, application: App): Unit {
        val aboutSettingsModel = AboutSettingsFeature.`get`.castIfSupported(model)
        val aboutSettingsApplication = AboutSettingsFeature.`get`.castIfSupported(application)
        if (aboutSettingsModel == null || aboutSettingsApplication == null) {
            return
        }
        assertBooleanEquals(aboutSettingsApplication.isAppVersionValid(), aboutSettingsModel.isAppVersionValid(), "Application version is incorrect")
        assertBooleanEquals(aboutSettingsApplication.isCopyrightValid(), aboutSettingsModel.isCopyrightValid(), "Copyright is incorrect")
    }

    open override fun tostring(): String {
        return "AboutSettingsComponent"
    }

    open override fun getComponentType(): String {
        return AboutSettingsComponent.type
    }

    companion object {
        @JvmStatic val type: String = "AboutSettingsComponent"
    }
}

public open class AllAboutSettingsActions: MBTComponentActions {
    open override fun getActions(model: App): YSArray<MBTAction> {
        val actions: YSArray<MBTAction> = mutableListOf()
        actions.add(CloseAboutSettingsAction())
        return actions
    }

}

