public open class SettingsIgnoredLanguageListComponent: MBTComponent {
    open override fun getComponentType(): MBTComponentType {
        return SettingsIgnoredLanguageListComponent.type
    }

    open override fun assertMatches(model: App, application: App): Unit {
        val modelTranslatorSettings = TranslatorSettingsFeature.`get`.castIfSupported(model)
        val appTranslatorSettings = TranslatorSettingsFeature.`get`.castIfSupported(application)
        if (modelTranslatorSettings != null && appTranslatorSettings != null) {
            val modelIgnoredTranslationLanguages = modelTranslatorSettings.getIgnoredTranslationLanguages()
            val appIgnoredTranslationLanguages = appTranslatorSettings.getIgnoredTranslationLanguages()
            assertInt32Equals(modelIgnoredTranslationLanguages.size, appIgnoredTranslationLanguages.size, "Number of ignored languages is incorrect")
            for (modelLanguage in modelIgnoredTranslationLanguages) {
                assertTrue(appIgnoredTranslationLanguages.contains(modelLanguage), "Application list of ignored languages not include ${modelLanguage}")
            }
        }
    }

    open override fun tostring(): String {
        return this.getComponentType()
    }

    companion object {
        @JvmStatic val type: String = "SettingsIgnoredLanguageListComponent"
    }
}

